        return new Source("{% extends 'base.html.twig' %}

{% block title %}Login{% endblock %}

{% block body %}
    {% if error %}
        <div>{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    <h1>Login</h1>

    <form action=\"{{ path('app_login') }}\" method=\"post\">
        <div class=\"mb-3\">
        <label for=\"username\" class=\"form-label required\">Username:</label>
        <input type=\"text\" id=\"username\" name=\"_username\" class=\"form-control\" value=\"{{ last_username }}\">
        </div>

        <div class=\"mb-3\">
        <label for=\"password\" class=\"form-label required\">Password:</label>
        <input type=\"password\" id=\"password\" class=\"form-control\" name=\"_password\">
        </div>

        <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">

        <div class=\"mb-3\">
            No account yet? <a href=\"{{ path('app_register') }}\" class=\"text-decoration-none\"> Register now</a>
        </div>
        {# If you want to control the URL the user is redirected to on success
        <input type=\"hidden\" name=\"_target_path\" value=\"/account\"> #}

        <button type=\"submit\" class=\"btn btn-primary\">Login</button>
    </form>
{% endblock %}", "login/index.html.twig", "/Users/alex/Schule/5bi-2022-23/Medientechnik/Content Management Systeme - Vertiefung/SymfonyORM/templates/login/index.html.twig");
